package com.bridgelabz.stringsarraysandregularexpression;

import com.utility.Utility;

public class UserDetailsUsingRegex {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Utility.regexExpression("dattatrya", "9158174621", "datta");
		Utility.details("Hello", "Java", "datta");
		Utility.replaceWithpattern("sushant", "Pratik");
		Utility.replaceAll("Hello","World");

	}

}
